import UIKit

protocol LoginBusinessLogic
{
    func checkLogin(request: Login.LoginCheck.Request)
}

protocol LoginDataStore
{
    var name: String { get set }
}

class LoginInteractor: LoginBusinessLogic, LoginDataStore
{
    var name = ""
    
    var presenter: LoginPresentationLogic?
    var worker: LoginWorker?
    //var name: String = ""
    
    // MARK: Do something
    
    func checkLogin(request: Login.LoginCheck.Request)
    {
        worker = LoginWorker()
        let isLoginValid = worker!.isLoginValid(name: request.name, password: request.password)
        
        let response = Login.LoginCheck.Response(isLoginValid: isLoginValid)
        presenter?.presentLoginFeedback(response: response)
    }
}
